package assigments;
/*8th pattern:

*****
*****
*****
*****
*****
*/
public class starPattern1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;int j;
		for(i=1;i<=5;i++) {
			for(j=1;j<=5;j++) {
			System.out.print( "* ");
		}
		System.out.println();
	}

	}

}
